import React from "react";
import PropertyList from "../userlist/index";
import UniqueID from "react-html-id";
import { makeStyles } from "@material-ui/styles";

import {
  Container,
  Row,
  Col,
  Form,
  Button,
  Table,
  ButtonGroup,
  Modal,
} from "react-bootstrap";

export default function Property(props) {
    const [id, setId] = React.useState(false);
    const [Name, setName] = React.useState("")
    const [Property, setProperty] = React.useState("");
    const [Description, setDescription] = React.useState("");
    const [modalShow, setModalShow] = React.useState(false);
    const [showPropId, setShowPropId] = React.useState(0);

    const data={
       data:{
        id: nextUniqueId(),
        Name: "shivraj",
        Property: "Flat",
        Size: "4bhk",
        Description: "Good Flat",

       },
       data:{
          id: nextUniqueId(),
          Name: "shivraj",
          Property: "Plot",
          Size: "4x7",

          Description: "Bad Plot",
        },
    }
    const [Data, setData] = React.useState(data);
const modelShow=(id)=>{
    setModalShow(true)
    setShowPropId(id)

}
const modalHide=()=>{
    setModalShow(false)


}

const handleSubmit=(e)=> {
    e.preventDefault();
    const dataList = {
        id: nextUniqueId(),
        Name: Name,
        Property: Property,
        Size: Size,

        Description:Description,
      };
      setData({
        data: [...data, dataList],
      });

}

const deleProperty=(index, e)=> {
    const emp = Object.assign([], data);
    emp.splice(index, 1);
    setData({
      data: emp,
    });
  }






    return (
      <div className="App" style={{ marginTop: "40px", marginBottom: "40px" }}>
        {data.length ? (
          <Modal
            show={modelShow}
            onHide={modalHide}
            size="lg"
            aria-labelledby="contained-modal-title-vcenter"
            centered
          >
            <Modal.Header closeButton>
              <Modal.Title id="contained-modal-title-vcenter">
                {data[showPropId].Name +
                  " " +
                  data[showPropId].Property}
              </Modal.Title>
            </Modal.Header>
            <Modal.Body>
              <Table striped bordered hover>
                <thead>
                  <tr>
                    <th  >Name</th>
                    <th >Property</th>
                    <th >Size</th>

                    <th >Description</th>
                  </tr>
                </thead>
                <tbody>
                  <tr>
                    <td >{data[showPropId].Name}</td>
                    <td >{data[showPropId].Property}</td>
                    <td >{data[showPropId].Size}</td>

                    <td >{data[showPropId].Description}</td>
                  </tr>
                </tbody>
              </Table>
            </Modal.Body>
          </Modal>
        ) : (
          ""
        )}
        <Container className={classes.Main}>
          <Row>
            <Col lg={{ span: "8", offset: "2" }}>
              <PropertyList
                propertyData={data}
                deleProperty={deleProperty}
                modalShow={modelShow}
              />
            </Col>
            <Col style={{width: "400px",height: "600px"}} lg={{ span: "8", offset: "2" }}>
              <h3>Add Property</h3>
              <br />
              <Form style={{width: "300px",height: "300px",border: "1px solid black"}} onSubmit={handleSubmit}>
                <Form.Group style={{width: "100px", marginTop:"10px"}}>
                  <Form.Label>Name</Form.Label>
                  <Form.Control style={{width: "200px", marginLeft:"50px"}}
                    value={Name}
                    type="text"
                    name="Name"
                    placeholder="Name"
                    onChange={(e) => setName(e.target.value)}
                  />
                </Form.Group>
                <Form.Group style={{width: "100px", marginTop:"10px"}} >
                  <Form.Label>Property</Form.Label>
                  <Form.Control style={{width: "200px", marginLeft:"50px"}}
                    type="text"
                    placeholder="Property Type"
                    name="Property"
                    value={Property}
                    onChange={(e) => setProperty(e.target.value)}
                  />
                </Form.Group>
                <Form.Group style={{width: "100px", marginTop:"10px"}} >
                  <Form.Label>Size</Form.Label>
                  <Form.Control style={{width: "200px", marginLeft:"50px"}}
                    value={Size}
                    type="text"
                    name="Size"
                    placeholder="size"
                    onChange={(e) => setSize(e.target.value)}
                  />
                </Form.Group>

                <Form.Group style={{width: "100px", marginTop:"10px"}} >
                  <Form.Label>Description</Form.Label>
                  <Form.Control style={{width: "200px", marginLeft:"50px"}}
                    type="text"
                    placeholder="About Property"
                    name="Description"
                    value={Description}
                    onChange={(e) => setDescription(e.target.value)}
                  />
                </Form.Group>
                <Button style={{width: "100px", marginTop:"10px"}} type="submit">Add Property</Button>
              </Form>
            </Col>
          </Row>
        </Container>
      </div>
    );
  }
